import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { FC } from 'react';
import { LocalizeText, RoomWidgetUpdateInfostandUserEvent } from '../../../../api';
import { Column, Flex, LayoutAvatarImageView, LayoutBadgeImageView, Text } from '../../../../common';

interface InfoStandWidgetBotViewProps
{
    botData: RoomWidgetUpdateInfostandUserEvent;
    close: () => void;
}

export const InfoStandWidgetBotView: FC<InfoStandWidgetBotViewProps> = props =>
{
    const { botData = null, close = null } = props;

    if(!botData) return null;

    return (
        <Column className="nitro-infostand rounded">
            <Column overflow="visible" className="container-fluid content-area" gap={ 1 }>
                <Column gap={ 1 }>
                    <Flex alignItems="center" justifyContent="between" gap={ 1 }>
                        <Text gfbold variant="white" wrap>{ botData.name }</Text>
                        <FontAwesomeIcon icon="times" className="cursor-pointer" onClick={ close } />
                    </Flex>
                    <hr className="m-0" />
                </Column>
                <Column gap={ 1 }>
                    <Flex gap={ 1 }>
                        <Column fullWidth className="body-image bot">
                            <LayoutAvatarImageView figure={ botData.figure } direction={ 4 } />
                        </Column>
                        <Column grow center gap={ 0 }>
                            { (botData.badges.length > 0) && botData.badges.map(result =>
                            {
                                return <LayoutBadgeImageView key={ result } badgeCode={ result } showInfo={ true } />;
                            }) }
                        </Column>
                    </Flex>
                    <hr className="m-0" />
                </Column>
                <Flex alignItems="center" className="py-1 px-2">
                    <Text fullWidth wrap textBreak variant="white" className="motto-content">{ botData.motto }</Text>
                </Flex>
                { (botData.carryItem > 0) &&
                    <Column gap={ 1 }>
                        <hr className="m-0" />
                        <Text variant="white" wrap>
                            { LocalizeText('infostand.text.handitem', [ 'item' ], [ LocalizeText('handitem' + botData.carryItem) ]) }
                        </Text>
                    </Column> }
            </Column>
        </Column>
    );
}
